INSERT INTO `wp_posts` VALUES("1036", "1", "2017-10-08 09:14:51", "2017-10-08 02:14:51", "Kệ tivi bằng gỗ xoan đào cũng được rất nhiều người tiêu dùng lựa chọn bởi màu gỗ sáng, kiểu dáng thiết kế đa dạng cùng với đó là giá thành rẻ hơn so với những loại gỗ tự nhiên khác. Những mẫu kệ tivi gỗ xoan đào với màu cánh gián đặc trưng sẽ tạo cảm giác ấm cúng cho <b>Nội thất phòng khách</b> nhà bạn.", "Kệ tivi gỗ xoan đào - KTV9", "", "publish", "open", "closed", "", "ke-tivi-go-xoan-dao-ktv9", "", "", "2017-10-10 14:47:00", "2017-10-10 07:47:00", "", "0", "http://dangquang.hunghaweb.com/?post_type=product&#038;p=1036", "0", "product", "", "0");
