INSERT INTO `wp_posts` VALUES("1117", "1", "2017-10-09 10:11:48", "2017-10-09 03:11:48", "<img class=\"alignnone size-medium wp-image-1118\" src=\"http://dangquang.hunghaweb.com/wp-content/uploads/2017/10/5-241x300.png\" alt=\"\" width=\"241\" height=\"300\" />\r\n\r\nCầu thang là một trong yếu tố đặc biệt để kết nối các không khí giữa những tầng lầu trong ngôi nhà của khách hàng.Vì vậy nó xừng đáng có một mẫu thi công đẹp như 1 vật trang trí hay như một tác phẩm thẩm mỹ và nghệ thuật thích mắt chứ không chỉ có một chiếc cầu thang thông thường phụ trách việc khiến cho bạn dịch rời lên xuống giữa những tầng trong nhà.", "Cầu thang sắt đẹp - S3", "", "publish", "open", "closed", "", "cau-thang-sat-dep-s3", "", "", "2017-10-13 10:38:31", "2017-10-13 03:38:31", "", "0", "http://dangquang.hunghaweb.com/?post_type=product&#038;p=1117", "0", "product", "", "0");
