INSERT INTO `wp_posts` VALUES("1121", "1", "2017-10-09 10:17:06", "2017-10-09 03:17:06", "<img class=\"alignnone size-medium wp-image-1122\" src=\"http://dangquang.hunghaweb.com/wp-content/uploads/2017/10/z790993063283_4ee9068bd59b2ef0c7cc2a09d90e476e-300x225.jpg\" alt=\"\" width=\"300\" height=\"225\" />\r\n\r\n<img class=\"alignnone size-medium wp-image-1123\" src=\"http://dangquang.hunghaweb.com/wp-content/uploads/2017/10/z790993548952_9fe2b40388bcc694705c9e143cbdb96d-300x225.jpg\" alt=\"\" width=\"300\" height=\"225\" />\r\n\r\n<img class=\"alignnone size-medium wp-image-1124\" src=\"http://dangquang.hunghaweb.com/wp-content/uploads/2017/10/z790993610255_da68d46c7a2eca726ffb4a2a969e02b5-300x225.jpg\" alt=\"\" width=\"300\" height=\"225\" />\r\n\r\nNếu bạn muốn một công trình cầu thang bền vững, chắc chắn và xinh xắn, ở đây, bạn sẽ không thể không nhắc đến sự góp mặt của tay vịn cầu thang gỗ. Được cấu thành từ chất liệu gỗ tốt như gỗ Lim Nam Phi, gỗ sồi Nga, gỗ sồi Mỹ, sản phẩm cho chất lượng vượt trội, đảm bảo an toàn, an tâm trong quá trình sử dụng.\r\nĐặc biệt, với một mức giá cực kỳ ưu đãi và hợp lý, tay vịn cầu thang gỗ chính là sự lựa chọn lý tưởng của bạn và cả gia đình. Sự hiện diện của chúng không chỉ khiến công trình cầu thang đẹp hơn mà trên hết, chúng chính là điểm nhấn hoàn hảo đối với tổng thể căn nhà.", "Cầu thang gỗ tay vịn - G6", "", "publish", "open", "closed", "", "cau-thang-go-tay-vin-g6", "", "", "2017-10-10 14:21:30", "2017-10-10 07:21:30", "", "0", "http://dangquang.hunghaweb.com/?post_type=product&#038;p=1121", "0", "product", "", "0");
